print(sum([1 if all([3 >= abs(diff) >= 1 for diff in [report[i] - report[i + 1] for i in range(len(report) - 1)]]) and (all([diff < 0 for diff in [report[i] - report[i + 1] for i in range(len(report) - 1)]]) or all([diff > 0 for diff in [report[i] - report[i + 1] for i in range(len(report) - 1)]])) else (1 if any([1 for ind in range(len(report)) if all([3 >= abs(diff) >= 1 for diff in [(report[:ind] + report[ind + 1:])[i] - (report[:ind] + report[ind + 1:])[i + 1] for i in range(len((report[:ind] + report[ind + 1:])) - 1)]]) and (all([diff < 0 for diff in [(report[:ind] + report[ind + 1:])[i] - (report[:ind] + report[ind + 1:])[i + 1] for i in range(len((report[:ind] + report[ind + 1:])) - 1)]]) or all([diff > 0 for diff in [(report[:ind] + report[ind + 1:])[i] - (report[:ind] + report[ind + 1:])[i + 1] for i in range(len((report[:ind] + report[ind + 1:])) - 1)]]))]) else 0) for report in [[int(t) for t in report.split()] for report in open("day2/day2.txt", "r").readlines()]]))
